// airtable.js

import { useState, useEffect } from 'react';
import { Box, Button, CircularProgress } from '@mui/material';
import axios from 'axios';

export const AirtableIntegration = ({ user, org, integrationParams, setIntegrationParams }) => {
    const [isConnected, setIsConnected] = useState(false);
    const [isConnecting, setIsConnecting] = useState(false);

    // Function to open OAuth in a new window and handle authorization
    const handleConnectClick = async () => {
        try {
            console.log('Initiating Airtable connection...');
            setIsConnecting(true);

            const formData = new FormData();
            formData.append('user_id', user);
            formData.append('org_id', org);

            const response = await axios.post('http://localhost:8000/integrations/airtable/authorize', formData);
            const authURL = response?.data;
            console.log('Received authorization URL:', authURL);

            const newWindow = window.open(authURL, 'Airtable Authorization', 'width=600, height=600');

            // Polling for the OAuth window to close
            const pollTimer = window.setInterval(() => {
                if (newWindow?.closed !== false) { 
                    console.log('OAuth window closed. Checking credentials...');
                    window.clearInterval(pollTimer);
                    handleWindowClosed();
                }
            }, 200);
        } catch (e) {
            console.error('Error during authorization:', e);
            setIsConnecting(false);
            alert(e?.response?.data?.detail || 'Authorization failed');
        }
    };

    // Function to handle logic when the OAuth window closes
    const handleWindowClosed = async () => {
        try {
            console.log('Fetching Airtable credentials...');
            const formData = new FormData();
            formData.append('user_id', user);
            formData.append('org_id', org);

            const response = await axios.post('http://localhost:8000/integrations/airtable/credentials', formData);
            const credentials = response.data;
            console.log('Received credentials:', credentials);

            if (credentials) {
                setIsConnected(true);
                setIntegrationParams(prev => ({ ...prev, credentials, type: 'Airtable' }));
            }
        } catch (e) {
            console.error('Error fetching credentials:', e);
            alert(e?.response?.data?.detail || 'Failed to fetch credentials');
        } finally {
            setIsConnecting(false);
        }
    };

    useEffect(() => {
        console.log('Checking initial connection status...');
        setIsConnected(!!integrationParams?.credentials);
    }, [integrationParams]);

    return (
        <Box sx={{ mt: 2 }}>
            <p>Parameters</p>
            <Box display='flex' alignItems='center' justifyContent='center' sx={{ mt: 2 }}>
                <Button 
                    variant='contained' 
                    onClick={isConnected ? undefined : handleConnectClick}
                    color={isConnected ? 'success' : 'primary'}
                    disabled={isConnecting}
                    style={{
                        pointerEvents: isConnected ? 'none' : 'auto',
                        cursor: isConnected ? 'default' : 'pointer',
                        opacity: isConnected ? 1 : undefined,
                    }}
                >
                    {isConnected ? 'Airtable Connected' : isConnecting ? <CircularProgress size={20} /> : 'Connect to Airtable'}
                </Button>
            </Box>
        </Box>
    );
};
