import { useState } from 'react';
import {
    Box,
    TextField,
    Button,
} from '@mui/material';
import axios from 'axios';
import logger from './services/logger';

const endpointMapping = {
    'Notion': 'notion',
    'Airtable': 'airtable',
    'HubSpot': 'hubspot',
};

export const DataForm = ({ integrationType, credentials }) => {
    const [loadedData, setLoadedData] = useState(null);
    const endpoint = endpointMapping[integrationType];

    const handleLoad = async () => {
        const startTime = performance.now();
        try {
            logger.logUserInteraction('DataForm', 'LOAD_DATA_CLICK', {
                integration_type: integrationType
            });

            const formData = new FormData();
            formData.append('credentials', JSON.stringify(credentials));

            const response = await axios.post(`http://localhost:8000/integrations/${endpoint}/load`, formData);
            const data = response.data;

            logger.logApiCall(
                'DataForm',
                `/integrations/${endpoint}/load`,
                'POST',
                startTime,
                response.status,
                {
                    integration_type: integrationType,
                    items_count: Array.isArray(data) ? data.length : 0
                }
            );

            setLoadedData(data);

            logger.info('DataForm', 'DATA_LOAD_SUCCESS', {
                integration_type: integrationType,
                items_count: Array.isArray(data) ? data.length : 0,
                duration_ms: performance.now() - startTime
            });
        } catch (e) {
            logger.error('DataForm', 'DATA_LOAD_ERROR', e, {
                integration_type: integrationType,
                endpoint: endpoint
            });
            alert(e?.response?.data?.detail);
        }
    }

    const handleClear = () => {
        logger.logUserInteraction('DataForm', 'CLEAR_DATA', {
            integration_type: integrationType
        });
        setLoadedData(null);
    }

    return (

        <div className="layout">
        <div className="content">
          <div className="page">
            <div className="bg_circle-wrapper">
              <div className="circle circle-one" />
              <div className="circle circle-two" />
              <div className="circle circle-three" />
            </div>
            <div className="page-content">
            <Box display='flex' justifyContent='center' alignItems='center' flexDirection='column' width='100%'>
           <Box display='flex' flexDirection='column' width='100%'>
             {/* <TextField
                    label="Loaded Data"
                    value={loadedData || ''}
                    sx={{mt: 2}}
                    InputLabelProps={{ shrink: true }}
                    disabled
                /> */}
                <Button
                    onClick={handleLoad}
                    sx={{mt: 2}}
                    variant='contained'
                >
                    Load Data
                </Button>
                <Button
                    onClick={handleClear}
                    sx={{mt: 1}}
                    variant='contained'
                >
                    Clear Data
                </Button>
            </Box>
        </Box>
            </div>
          </div>
        </div>
      </div>
    );
}
